mapper.insert:

	SELECT SEQ_SUBSCRIPTION_NO.NEXTVAL FROM DUAL

INSERT INTO SUBSCRIPTIONS (
    SUBSCRIPTION_NO
    , CONTACT_ID
    , PAYMENT_STATUS
    , SUBSCRIPTION_START
    , SUBSCRIPTION_END
    , SUBSCRIPTIONS_ACTIVE
    , PLAN_TYPE
    , BILLING_DATE
    , BILLING_KEY_ID
) VALUES (
    #{subscriptionNo}
  , #{contactId}
  , #{paymentStatus}
  , #{subscriptionStart}
  , #{subscriptionEnd}
  , #{subscriptionsActive}
  , #{planType}
  , #{billingDate}
  , #{billingKeyId}
)

mapper.update:
UPDATE SUBSCRIPTION_PLANS
SET MONTHLY_PRICE = #{monthlyPrice},
    ANNUAL_PRICE = #{annualPrice},
    MAINTAIN_OLD_PRICE = #{maintainOldPrice},
    MAXIMUM_PEOPLE = #{maximumPeople}
WHERE PLAN_NO = #{planNo}
